  //     },
  //     body: JSON.stringify({ post: this.state.post }),
  //   });
  //   const body = await response.text();

  //   this.setState({ responseToPost: body });
  // };

render() {
    return (
      <div className="App">
        <CustomNavBar />
        <Container fluid>
          <Dayquote quote={this.state.response}/>
        </Container>
        <ReactLive2d
          width = { 300}
          height = { 500}
          bottom = { '10px'}
          right = { '50px'}
          ModelList = { ['Rice']}
          TouchBody = { ['What are you doing?', 'Humph', 'Bad boy']}
          PathFull='http://publicjs.supmiao.com/Resources/'
        />
        <p>yo </p>
      </div>
    );
  }
}

export default App;
